
function intro(player) {
"use strict";

  function initialize() {
    canvc.width  = 352;
    canvc.height = 256;
    canvx.mozImageSmoothingEnabled = false;
    canvx.imageSmoothingEnabled = false;

    canvx.fillStyle = "#003131";
    canvx.fillRect(0,218,352,1);

    canvx.fillStyle = "#fff";
    canvx.fillRect(0,8,352,1);
    canvx.fillRect(0,219,352,1);

      buf1c.width  = 592;
    buf1c.height = 208;
    buf1x.mozImageSmoothingEnabled = false;
    buf1x.imageSmoothingEnabled = false;

    buf2c.width  = 352;
    buf2c.height = 246;
    buf2x.mozImageSmoothingEnabled = false;
    buf2x.imageSmoothingEnabled = false;

    center(0);

    document.getElementById("intro").appendChild(canvc);

   
      blitter(0);
  
  }

  function center(index) {
    var i = index;
    var l = i + 40;
    var tot = 0;
    var chr;

    for (; i < l; i++) {
      chr = sc_text.charCodeAt(i) - 32;

      if (chr == 32) {
        sc_cx = (352 - tot) >> 1;
        break;
      }

      tot += sc_size[chr];
    }
  }

  function draw() {
    var cp = 0;
    var cy = sine_idx;
    var chr;

    buf1x.fillStyle = "#003131";
    buf1x.fillRect(0,0,buf1c.width,buf1c.height);

    do {
      buf1x.drawImage(logo, cp*80,sine[cy],80,208, cp*64,0,80,208);
      cy += 8;
      if (cy >= 60) { cy -= 60; }
    } while (++cp < 9);

    cp = 0;

    do {
      canvx.drawImage(buf1c, 0,cp,592,1, -cp-16,10+cp,592,1);
    } while (++cp < 208);

    if (++sine_idx >= 60) {
      sine_idx = 0;
    }

    if (++sc_delay >= 20) {
      sc_delay = 0;
      cp = 0;

      do {
        chr = sc_text.charCodeAt(sc_pos) - 32;
        if (++sc_pos >= sc_len) { sc_pos = 0; }

        if (chr == 32) {
          center(sc_pos);
          break;
        }

        buf2x.drawImage(font, chr*16,0,16,20, sc_cx,228,16,20);
        sc_cx += sc_size[chr];
      } while (++cp < 40);
    }

    buf2x.globalCompositeOperation = "copy";
    buf2x.drawImage(buf2c, 0,1,352,245, 0,0,352,245);
    buf2x.globalCompositeOperation = "source-over";

    canvx.drawImage(buf2c, 0,0,352,208, 0,10,352,208);
  }

  function blitter(now) {
    var delta = now - before;
    step_id = window.requestAnimationFrame(blitter);

    if (delta >= interval) {
      draw();
      before = now - (delta % interval);
    }
  }

  const interval = 1000 / 50;

 const font = new Image();
  const logo = new Image();

    font.src = "data:image/png;base64,";
  logo.src = "data:image/png;base64,";

  const canvc = document.createElement("canvas");
  const canvx = canvc.getContext("2d", {alpha: false});
  const buf1c = document.createElement("canvas");
  const buf1x = buf1c.getContext("2d", {alpha: false});
  const buf2c = document.createElement("canvas");
  const buf2x = buf2c.getContext("2d");

  const sine = new Uint16Array([128,135,141,148,154,160,166,171,176,180,183,186,189,191,192,192,192,191,189,186,183,180,176,171,166,160,154,148,141,135,128,121,115,108,102,96,90,85,80,76,73,70,67,65,64,64,64,65,67,70,73,76,80,85,90,96,102,108,115,121]);

  const sc_text = "WELCOME TO AUX SERVER 1!@@@@BUT THERE REALLY@IS NOTHING TO SEE HERE...@@@PLEASE MOVE ALONG!@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@";
  const sc_len  = sc_text.length;
  const sc_size = new Uint8Array([8,5,6,10,16,16,16,4,9,9,16,9,6,9,5,10,13,5,13,13,13,13,13,15,15,13,5,6,8,9,8,14,13,13,13,13,12,13,9,12,13,5,12,13,13,13,10,13,13,13,13,15,12,10,14,15,13,13,15]);

  var before  = 0;
  var step_id = 0;

  var sine_idx = 0
  var sc_cx    = 0;
  var sc_delay = 0;
  var sc_pos   = 0;

  initialize();
}